package testpackage;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String X="Water";
		String Y="kool-aid";
		String temp;
		System.out.println("X: "+X);
		System.out.println("Y: "+Y);
		temp=X;
		X=Y;
		Y=temp;
		System.out.println("X: "+X);
		System.out.println("Y: "+Y);
	}

}
